import React from 'react';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import NavBar from './components/NavBar'
import PostPane from './components/PostPane'
import "bootswatch/dist/custom/bootstrap.min.css";
import "./css/custom.css";

const styles = {
  container: {

  },
  right: {
    height: window.innerHeight, // I want to change this
    padding: 0,
    margin: 0,
    overflow: 'hidden',
    backgroundColor: 'yellow'
  },
  left: {
    overflowY: '100%',
    padding: 0,
    height: window.innerHeight,
    paddingBottom: '50px',
    backgroundColor: 'white'
  },
  row: {
    marginBottom: 0
  }
}


class App extends React.Component {
  render() {
      return (
        <div className="App">
          <NavBar />
          <div className="container-row stretch">
            <Col className="container-col no-padding" styles={styles.left}>
              <PostPane />
            </Col>
            <Col className="container-row no-padding">
              right
            </Col>
          </div>
        </div>
      );
  }
}

export default App;
