import React, { useState } from "react";
import Modal from "react-bootstrap/Modal"
import InputGroup from "react-bootstrap/InputGroup"
import Form from "react-bootstrap/Form"
import Button from "react-bootstrap/Button"
import Col from "react-bootstrap/Col"
import Row from "react-bootstrap/Row"

export default class NewPost extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      room: "",
      building: null,
      image: null,
      description: "",
      diet: {
        "vegetarian": false,
        "vegan": false,
        "kosher": false,
        "halal": false,
        "gluten": false,
      },
      feeds: ""
    };
  }

  handleChange = event => {
    const name = event.target.name;
    const value = event.target.value;
    this.setState({ [name]: value });
  }

  handleDietChange = event => {
    const name = event.target.name;
    const diet = this.state.diet;
    diet[name] = !diet[name];
    this.setState({ diet });
  }

  reset() {
    this.props.handleClose();

    const diet = {
      "vegetarian": false,
      "vegan": false,
      "kosher": false,
      "halal": false,
      "gluten": false,
    };

    this.setState({
      title: "",
      room: "",
      building: null,
      image: null,
      description: "",
      diet,
      feeds: ""
    });
  }

  handleSubmit = event => {
    event.preventDefault();
    this.props.handleClose();

    const post = {
      "id": 4,
      "title": this.state.title,
      "room": this.state.room,
      "building": this.state.building,
      "description": this.state.description,
      "diet": [0, 3],
      "feeds": this.state.feeds
    };

    axios.post('https://my-json-server.typicode.com/bcatoto/freat/posts', { user })
      .then(res => {
        console.log(res);
        console.log(res.data);
      });

    this.reset();
  }

  renderBuildings() {
    const buildings = ["-- Select building --", "Bloomberg Hall", "Dod Hall", "Colonial", "Friend Center"];
    return buildings.sort()
      .map((building, index) =>
        <option key={index}>{building}</option>
      );
  }

  renderDietOptions() {
    const diets = [
      {
        "name": "vegetarian",
        "label": "Vegetarian"
      },
      {
        "name": "vegan",
        "label": "Vegan"
      },
      {
        "name": "kosher",
        "label": "Kosher"
      },
      {
        "name": "halal",
        "label": "Halal"
      },
      {
        "name": "gluten",
        "label": "Gluten-Free"
      }
    ];

    return diets.map((diet, index) =>
      <Form.Check
        key={index}
        type="checkbox"
        name={diet.name}
        label={diet.label}
        onChange={this.handleDietChange}
      />
    );
  }

  render() {
    return (
      <Modal show={this.props.show}>
        <Modal.Header>
          <Modal.Title>New post</Modal.Title>
        </Modal.Header>
        <Form onSubmit={this.handleSubmit}>
          <Modal.Body>
            <Form.Group>
              <Form.Label>Title</Form.Label>
              <Form.Control type="text" name="title" placeholder="Enter title" onChange={this.handleChange}/>
            </Form.Group>

            <Form.Row>
              <Form.Group as={Col}>
                <Form.Label>Room</Form.Label>
                <Form.Control type="text" name="room" placeholder="Enter room" onChange={this.handleChange}/>
              </Form.Group>

              <Form.Group as={Col} controlId="input-building">
                <Form.Label>Building</Form.Label>
                <Form.Control as="select" name="building" onChange={this.handleChange}>
                  {this.renderBuildings()}
                </Form.Control>
              </Form.Group>
            </Form.Row>

            <Form.Group controlId="input-image">
              <Form.Label>Image</Form.Label>
              <Form.Control type="file" />
              <Form.Control.Feedback type="invalid">Please upload an image.</Form.Control.Feedback>
            </Form.Group>

            <Form.Group controlId="input-desc">
              <Form.Label>Description</Form.Label>
              <Form.Control as="textarea" name="description" rows="3" onChange={this.handleChange}/>
            </Form.Group>

            <Form.Group controlId="input-diet">
              <Form.Label>Dietary Options</Form.Label>
              {this.renderDietOptions()}
            </Form.Group>

            <Form.Group controlId="input-feeds">
              <Form.Label>Feeds approximately...</Form.Label>
              <Form.Control type="number" name="feeds" placeholder="1" onChange={this.handleChange}/>
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button type="reset" variant="danger" className="mr-1" onClick={() => this.reset()}>Cancel</Button>
            <Button type="submit" variant="outline-primary">Submit</Button>
          </Modal.Footer>
        </Form>
      </Modal>
    );
  }
}
