import React from "react";
import Accordion from "react-bootstrap/Accordion"
import Card from "react-bootstrap/Card"
import Badge from "react-bootstrap/Badge"

import berries from "./../assets/images/berries.png"

export default class Post extends React.Component {
  renderDietOptions() {
    const info = [
      {
        "key": 0,
        "name": "Vegetarian",
        "color": "vegetarian",
      },
      {
        "key": 1,
        "name": "Vegan",
        "color": "vegan",
      },
      {
        "key": 2,
        "name": "Kosher",
        "color": "kosher",
      },
      {
        "key": 3,
        "name": "Halal",
        "color": "halal",
      },
      {
        "key": 4,
        "name": "Gluten-Free",
        "color": "gluten",
      }
    ];

    const diets = this.props.diet.map(i => info[i]);
    return diets.map(diet =>
      <Badge pill key={diet.key} variant={diet.color}>{diet.name}</Badge>
    );
  }

  render() {
    return (
      <Accordion.Toggle as={Card} eventKey={this.props.id}>
        <Card.Header>
          <Card.Title as={Col}>{this.props.title}</Card.Title>
          <Col>
            <Row>Location</Row>
            <Row>Time</Row>
          </Col>
        </Card.Header>
        <Card.Img src={berries} />
        <Accordion.Collapse eventKey={this.props.id}>
          <Card.Body>
            {this.props.body}
            <br/>
            <em>Feeds approximately: {this.props.feeds}</em>
          </Card.Body>
        </Accordion.Collapse>
        <Card.Footer>
          {this.renderDietOptions()}
        </Card.Footer>
      </Accordion.Toggle>
    );
  }
}
