import React from "react";
import Accordion from "react-bootstrap/Accordion"
import Card from "react-bootstrap/Card"
import Badge from "react-bootstrap/Badge"

import berries from "./../assets/images/berries.png"

export default class Post extends React.Component {
  renderDietOptions() {

  }

  render() {
    return (
      <Accordion.Toggle as={Card} eventKey={props.id}>
        <Card.Header>
          <Card.Title>{props.title}</Card.Title>
        </Card.Header>
        <Card.Img src={berries} />
        <Accordion.Collapse eventKey={props.id}>
          <Card.Body>
            {props.body}
          </Card.Body>
        </Accordion.Collapse>
        <Card.Footer>
          <Badge pill variant="vegetarian">Vegetarian</Badge>
          <Badge pill variant="vegan">Vegan</Badge>
          <Badge pill variant="kosher">Kosher</Badge>
          <Badge pill variant="halal">Halal</Badge>
          <Badge pill variant="gluten">Gluten-Free</Badge>
        </Card.Footer>
      </Accordion.Toggle>
    );
  }
}
