import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

import PostPane from "./PostPane";
import MapPane from "./MapPane";
import NewPost from "./NewPost";

import axios from "axios";

import "bootswatch/dist/custom/bootstrap.min.css";
import "./../css/custom.css";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      posts: []
    };
  }

  componentDidMount() {
    axios.get('https://my-json-server.typicode.com/bcatoto/freat/posts')
      .then(res => {
        const posts = res.data;
        this.setState({ posts });
      })
  }

  handleOpenModal() {
    this.setState({
      showModal: true
    });
  }

  handleCloseModal() {
    this.setState({
      showModal: false
    });
  }

  render() {
    return (
      <>
        <Container fluid className="p-0">
          <Row fluid="true" noGutters="true" className="h-100">
            <Col id="post-pane" xs={12} sm={5}>
              <PostPane posts={this.state.posts}/>
            </Col>
            <Col id="map-pane">
              <MapPane />
            </Col>
          </Row>
        </Container>
        <NewPost show={this.state.showModal} handleClose={() => this.handleCloseModal()}/>
      </>
    );
  }
}

export default Profile;
